var packageInfo = require('../../package.json');

module.exports = help;

function help () {
    console.log([
        packageInfo.name + ' ' + packageInfo.version,
        packageInfo.description,
        'Tries to find build.json and gulpfile.js in specified project DIR or uses default ones.',
        '',
        'Usage:',
        '\tym-builder [build] [DIR=.]\t\t\t# Runs gulp task "ym-build" described by local or default gulpfile.js.',
        '\tym-builder watch [DIR=.]\t\t\t# Setups gulp task "ym-watch" that will run "ym-build" on any change of source files specified in build.json.',
        '\tym-builder configure [DIR=.] [build.json] [gulpfile.js] [-f]\t# Makes a copy of default build.json and/or gulpfile.js in specified directory.',
        '\tym-builder help\t\t\t\t\t# Displays this message.'
    ].join('\n'));
}